import { useState } from 'react';
import './App.css';

function App() {
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [gender, setGender] = useState("")
  const [phonenumber, setPhoneNumber] = useState("")
  const [password, setPassword] = useState("")
  const [cpassword, setCpassword] = useState("")
  const [dob, SetDob] = useState("")
  const [username, SetUsername] = useState("")
  const [nameError, setnameError] = useState("")
  const [emailError, setemailError] = useState("")
  const [phoneNumberError, setphoneNumberError] = useState("")
  const [genderError, setgenderError] = useState("")
  const [emptyError, setemptyError] = useState("")
  const validate = () => {
    if (name === "" || email === "" || gender === "" || phonenumber === "" || password === "") {
      setemptyError("All fields are Mandatory")
      SetUsername("")
      return false
    }
    if (!name.match(/^[A-Za-z0-9- ]+$/)) {
      setnameError("Name is not Alphanumeric")
      SetUsername("")
      return false
    }
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    const isValid = validate()
  }
  return (
    <div className="container">
      <h1 className='text-center fw-bold'>Registration Form</h1>
      <div className='col-lg-8 m-auto d-block'>
      <form onSubmit={handleSubmit} className="bg-light">
        <div className='form-group'>
          <label htmlFor="">Name:</label>
          <input type="text" className='form-control' placeholder='Name' value={name} />
        </div>
        <div className='form-group'>
          <label htmlFor="">Email:</label>
          <input type="email" className='form-control' placeholder='Email' value={email} />
        </div>
        <div className='form-group'>
          <label htmlFor="">Password:</label>
        <input type="Password" className='form-control' placeholder='Password' value={password} />
        </div>
        <div className='form-group'>
          <label htmlFor="">Confirm Password:</label>
        <input type="Password" className='form-control' placeholder='Confirm Password' value={cpassword} />
        </div>
        <div className='form-group'>
          <label htmlFor="">Gender:</label>
          <select className='form-control' name="gender">
          <option value="">Male</option>
          <option value="">Female</option>
          <option value="">Other</option>
        </select>
        </div>
        <div>
          <label htmlFor="">DOB:</label>
          <input className='form-control' type="date" value={dob}/>
        </div>
        <div className="form-group">
          <label htmlFor="">Mobile Number:</label>
        <input type="text" className='form-control' placeholder='Phone Number' value={phonenumber} />
        </div>
        <div>
          <label htmlFor="">Address:</label>
          <textarea cols="30" rows="10"></textarea>
        </div>



        <span>{emptyError}</span>
        <button type='submit' value="Submit">Submit</button>
      </form>
      </div>
    </div>
  );
}

export default App;
