import { useState } from 'react';
import './App.css';

function App() {
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [gender, setGender] = useState("Male")
  const [phonenumber, setPhoneNumber] = useState("")
  const [password, setPassword] = useState("")
  const [cpassword, setCpassword] = useState("")
  const [address, setAddress] = useState("")
  const [dob, SetDob] = useState("")
  const [uphoto,setUPhoto] = useState("")
  const [usignature,setUSignature] = useState("")
  const [username, SetUsername] = useState("")
  const [uphotoError,setUPhotoError] = useState("")
  const [usignatureError,setUSignatureError] = useState("")
  const [addressError, setAddressError] = useState("")
  const [passwordError, setPasswordError] = useState("")
  const [cpasswordError, setCpasswordError] = useState("")
  const [dobError, SetDobError] = useState("")
  const [nameError, setnameError] = useState("")
  const [emailError, setemailError] = useState("")
  const [phoneNumberError, setphoneNumberError] = useState("")
  const [genderError, setgenderError] = useState("")
  const [emptyError, setemptyError] = useState("")
  const validate = () => {
    // if (name === "" || email === "" || gender === "" || phonenumber === "" || password === "") {
    //   setemptyError("**All fields are Mandatory")
    //   SetUsername("")
    //   return false
    // }
    if(name === ""){
      setnameError("**Please fill your Name")
      SetUsername("")
      return false
    } 
    else if (!name.match(/^[A-Za-z0-9- ]+$/)) {
      setnameError("**Name is not Alphanumeric")
      SetUsername("")
      return false
    }
    if(email === ""){
      setemailError("**Please fill the Email Id")
      SetUsername("")
      return false
    } 
    if(passwordError === ""){
      setPasswordError("**Please Enter the Password")
      SetUsername("")
      return false
    } 
    if(cpasswordError === ""){
      setCpasswordError("**Please Enter the Confirm Password")
      SetUsername("")
      return false
    } 
    if(dobError === ""){
      SetDobError("**Please fill your Date of Birth")
      SetUsername("")
      return false
    } 
    if(genderError === ""){
      setgenderError("**Please Select the Gender")
      SetUsername("")
      return false
    }
    if(phoneNumberError === ""){
      setphoneNumberError("**Please Select the Gender")
      SetUsername("")
      return false
    }
    if(addressError === ""){
      setAddressError("**Please Select the Gender")
      SetUsername("")
      return false
    }
    if(uphotoError === ""){
      setUPhotoError("**Please Select the Gender")
      SetUsername("")
      return false
    }
    if(usignatureError === ""){
      setUSignatureError("**Please Select the Gender")
      SetUsername("")
      return false
    } 
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    const isValid = validate()
  }
  return (
    <div className="container">
      <h1 className='text-center fw-bold'>Registration Form</h1>
      <div className='col-lg-8 m-auto d-block border border-dark-subtle'>
      <form onSubmit={handleSubmit} className="bg-light">
        <div className='form-group'>
          <label htmlFor="">Name:</label>
          <input type="text" className='form-control' placeholder='Name' value={name} onChange={(e)=>{setName(e.target.value)}}/>
          <span className="text-danger fw-bold">{nameError}</span>
        </div>
        <div className='form-group'>
          <label htmlFor="">Email:</label>
          <input type="email" className='form-control' placeholder='Email' value={email} onChange={(e)=>{setEmail(e.target.value)}}/>
          <span className="text-danger fw-bold">{emailError}</span>
        </div>
        <div className='form-group'>
          <label htmlFor="">Password:</label>
        <input type="Password" className='form-control' placeholder='Password' value={password} onChange={(e)=>{setPassword(e.target.value)}}/>
        <span className="text-danger fw-bold">{passwordError}</span>
        </div>
        <div className='form-group'>
          <label htmlFor="">Confirm Password:</label>
        <input type="Password" className='form-control' placeholder='Confirm Password' value={cpassword} onChange={(e)=>{setCpassword(e.target.value)}}/>
        <span className="text-danger fw-bold">{cpasswordError}</span>
        </div>
        <div className='form-group'>
          <label htmlFor="">Gender:</label>
          <select className='form-control' name="gender">
          <option value={gender} onChange={(e)=>{setGender(e.target.value)}}>Male</option>
          <option value={gender} onChange={(e)=>{setGender(e.target.value)}}>Female</option>
          <option value={gender} onChange={(e)=>{setGender(e.target.value)}}>Other</option>
          <span className="text-danger fw-bold">{genderError}</span>
        </select>
        </div>
        <div>
          <label htmlFor="">DOB:</label>
          <input className='form-control' type="date" value={dob} onChange={(e)=>{SetDob(e.target.value)}}/>
          <span className="text-danger fw-bold">{dobError}</span>
        </div>
        <div className="form-group">
          <label htmlFor="">Mobile Number:</label>
        <input type="text" className='form-control' placeholder='Phone Number' value={phonenumber} onChange={(e)=>{setPhoneNumber(e.target.value)}}/>
        <span className="text-danger fw-bold">{phoneNumberError}</span>
        </div>
        <div className='form-group'>
          <label htmlFor="">Address:</label>
          <textarea  className='form-control' value={address} rows="2" onChange={(e)=>{setAddress(e.target.value)}}></textarea>
          <span className="text-danger fw-bold">{addressError}</span>
        </div> 
        <div className='form-group'>
          <label htmlFor="">Upload Photo:</label>
          <input type="file" value={uphoto} className='form-control'/>
          <span className="text-danger fw-bold">{uphotoError}</span>
        </div>
        <div className='form-group'>
          <label htmlFor="">Upload Signature:</label>
          <input type="file" value={usignature} className='form-control'/>
          <span className="text-danger fw-bold">{usignatureError}</span>
        </div>
        {/* <span className="text-danger fw-bold">{emptyError}</span> */}

        <br />
        <button type='submit' value="Submit" className='btn btn-success'>Submit</button>
        <button type='reset' className='btn btn-danger'>Clear</button>
      </form>
      </div>
      <div>
        <h2>{username ? "Hello" + username : ""}</h2>
      </div>
    </div>
  );
}

export default App;
